mapper.select:
SELECT COUNT(DISTINCT p.id)
FROM Post p
LEFT JOIN ThumbsUp t ON p.id = t.postId




                p.title LIKE CONCAT('%', #{searchValue}, '%')


                p.content LIKE CONCAT('%', #{searchValue}, '%')


                p.domain = #{searchValue}


                p.hashtag LIKE CONCAT('%', #{searchValue}, '%')




        AND p.displayAt >= #{displayAt}


        AND p.displayEnd <= #{displayEnd}

    AND p.userId = #{userId}

SELECT p.*, COUNT(t.id) as thumbsUpCount    
FROM Post p
LEFT JOIN ThumbsUp t ON p.id = t.postId




                p.title LIKE CONCAT('%', #{searchValue}, '%')


                p.content LIKE CONCAT('%', #{searchValue}, '%')


                p.domain = #{searchValue}


                p.hashtag LIKE CONCAT('%', #{searchValue}, '%')




        AND p.displayAt >= #{displayAt}


        AND p.displayEnd <= #{displayEnd}

    AND p.userId = #{userId}

GROUP BY p.id


        ORDER BY thumbsUpCount DESC, p.createdAt DESC


        ORDER BY p.createdAt DESC


LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM Post
WHERE id = #{id} AND userId = #{userId}

SELECT s.id, p.id, p.title, p.createdAt, p.userId, s.createdAt AS sharedCreatedAt FROM Share s JOIN Post p ON s.postId = p.id
p.title LIKE CONCAT('%', #{searchValue}, '%')
p.content LIKE CONCAT('%', #{searchValue}, '%')
p.userId LIKE CONCAT('%', #{searchValue}, '%')
AND p.displayAt >= #{displayAt}
AND p.displayEnd <= #{displayEnd}
AND s.userId = #{userId}
ORDER BY p.createdAt DESC LIMIT #{offset}, #{limit}

SELECT id as postId, title, createdAt
FROM Post




                title LIKE CONCAT('%', #{searchValue}, '%')


                content LIKE CONCAT('%', #{searchValue}, '%')


                userId LIKE CONCAT('%', #{searchValue}, '%')




        AND p.displayAt >= #{displayAt}


        AND p.displayEnd <= #{displayEnd}

    AND userId = #{userId}

ORDER BY createdAt DESC
LIMIT #{offset}, #{limit}
